mapper.select:
SELECT memberIdx, memberID, memberPW, memberName, memo from member


		memberID LIKE '%${memberID}%'


		AND memberName LIKE '%${memberName}%'


		AND memo LIKE '%${memo}%'


		AND memberIdx = #{memberIdx}


		AND memberPW LIKE #{memberPW}

mapper.update:
UPDATE member
SET memberName=#{memberName}, memo=#{memo}, memberID=#{memo}, memberPW=#{memo}
WHERE memberIdx=#{memberIdx}
