mapper.select:
SELECT * from payment
WHERE isDelete = 0

	AND payIdx = #{payIdx}


	AND memberIdx = #{memberIdx}


	AND payCode = #{payCode}


	AND lessonCnt = #{lessonCnt}


	AND practiceCnt = #{practiceCnt}


	AND payDate like '%${payDate}%'


	AND lessonRmnCnt = #{lessonRmnCnt}


	AND practiceRmnCnt = #{practiceRmnCnt}


	AND memo like '%${memo}%'


	AAND usedRsvIdx like '%${usedRsvIdx}%'


	AND fees = #{fees}
